'use strict';

var typeOf = require('kind-of');

module.exports = function filterType(arr, type) {
  var len = arr.length;
  var res = [];
  var i = 0;

  while (len--) {
    var ele = arr[i++];
    if (typeOf(ele) === type) {
      res.push(ele);
    }
  }
  return res;
};
